resource "zia_firewall_dns_rule" "this" {
    name = "Example_DNS_Rule01"
    description = "Example_DNS_Rule01"
    action = "REDIR_REQ"
    state = "ENABLED"
    order = 10
    rank = 7
    redirect_ip = "8.8.8.8"
    dest_countries = ["CA", "US"]
    source_countries = ["CA", "US"]
    protocols = ["ANY_RULE"]
    departments {
        id = [ data.zia_department_management.engineering.id ]
