  state                        = "ENABLED"
  order                        = 1
  rank                         = 7
  road_warrior_for_kerberos    = true
  cloud_applications           = ["CHATGPT_AI", "ANDI"]
  platforms                    = ["SCAN_IOS", "SCAN_ANDROID", "SCAN_MACOS", "SCAN_WINDOWS", "NO_CLIENT_CONNECTOR", "SCAN_LINUX"]

  action {
    type                                    = "DO_NOT_DECRYPT"
